package controlstatements;

public class NumberIsEvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 4;

        if (number % 2 == 0) {
            System.out.println("The number is even.");
        } else {
            System.out.println("The number is odd.");
        }
	}
}
