package operators;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;

		num += 6;
		System.out.println("After += : " + num);

		num -= 2;
		System.out.println("After -= : " + num);

		num *= 3;
		System.out.println("After *= : " + num);

		num /= 5;
		System.out.println("After /= : " + num);

		num %= 3;
		System.out.println("After %= : "+ num);

	}

}
